import Head from 'next/head'
import {useEffect,useState} from "react"
import styles from '../styles/Home.module.css'
import { urlObjectKeys } from 'next/dist/next-server/lib/utils';

export default function Home() {

  const [scrollPosition, setSrollPosition] = useState(0);
const handleScroll = () => {
    const position =window&&window.pageYOffset;
    setSrollPosition(position);
};

useEffect(() => {
   window&&window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window&&window.removeEventListener('scroll', handleScroll);
    };
}, []);

let plus=window&&window.innerWidth*70/100
let pos=false
if(scrollPosition>320){
pos=true

}
  return (
    <div className="container2">
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
<div className="grid" style={pos?{width:scrollPosition+plus,position:"fixed"}:{width:scrollPosition+plus}}>

<img></img>

<img src={"https://www.apple.com/v/macbook-pro-16/b/images/overview/display_pro_fallback__fkg2i8si2wyi_medium.jpg"}></img>
</div>
    
      <footer className={styles.footer}>
        <a
          href="https://vercel.com?utm_source=create-next-app&utm_medium=default-template&utm_campaign=create-next-app"
          target="_blank"
          rel="noopener noreferrer"
        >
       By orkunmertgs41@gmail.com
        </a>
      </footer>
    </div>
  )
}
