import Head from 'next/head'
import {useEffect,useState} from "react"
import styles from '../styles/Home.module.css'
import { urlObjectKeys } from 'next/dist/next-server/lib/utils';

export default function Home() {

  const [scrollPosition, setSrollPosition] = useState(0);
  const [fixed, set_fixed] = useState(false);
  const [width, set_width] = useState(0);
const handleScroll = () => {



  const position = window.pageYOffset;
    setSrollPosition(position);
console.log(position)
};

useEffect(() => {
    window.addEventListener('scroll', handleScroll, { passive: true });
  
    return () => {
        window.removeEventListener('scroll', handleScroll);
    };
}, []);

useEffect(() => {
  if(!fixed&&scrollPosition>450){
    set_fixed(true)
  }
  
  if(fixed&&scrollPosition<=450){
    console.log("s",scrollPosition)
    set_fixed(false)
  }
    if(width!==window.innerWidth*70/100)
      set_width(window.innerWidth*70/100)
}, [scrollPosition]);


  return (
    <div className="container2">
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
<div className="grid" style={{width:scrollPosition+width,position:`${fixed?"fixed":""}`,top:0}}>

<img 
style={{width:"100%",height:`${scrollPosition?scrollPosition-490:0}px`,position:"fixed",bottom:0,objectFit:"contain"}}
src={"https://www.apple.com/v/macbook-pro-16/b/images/overview/display_hero_screen__eik3891ojtoy_large.jpg"}></img>

<img src={"https://www.apple.com/v/macbook-pro-16/b/images/overview/display_pro_fallback__fkg2i8si2wyi_medium.jpg"}></img>
</div>
    
      <footer className={styles.footer}>
        <a
          href="https://vercel.com?utm_source=create-next-app&utm_medium=default-template&utm_campaign=create-next-app"
          target="_blank"
          rel="noopener noreferrer"
        >
       By orkunmertgs41@gmail.com
        </a>
      </footer>
    </div>
  )
}
