import Head from 'next/head'
import {useEffect,useState} from "react"
import styles from '../styles/Home.module.css'
import { urlObjectKeys } from 'next/dist/next-server/lib/utils';

export default function Home() {

  const [scrollPosition, setSrollPosition] = useState(0);
  const [fixed, set_fixed] = useState(false);
  const [width, set_width] = useState(0);
const handleScroll = () => {



  const position = window.pageYOffset;
    setSrollPosition(position);
console.log(position)
};

useEffect(() => {
    window.addEventListener('scroll', handleScroll, { passive: true });
  
    return () => {
        window.removeEventListener('scroll', handleScroll);
    };
}, []);

useEffect(() => {
  if(!fixed&&scrollPosition>345&&scrollPosition<1500){
    set_fixed(true)
  }
  
  if(fixed&&(scrollPosition<=345||scrollPosition>1500)){
    console.log("s",scrollPosition)
    set_fixed(false)
  }
    if(width!==window.innerWidth*70/100)
      set_width(window.innerWidth*70/100)
}, [scrollPosition]);


  return (
    <div className="container2" style={{position:"relative"}}>
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

{scrollPosition<1400?(
  <div className="grid" style={{width:scrollPosition+width,position:`${fixed?"fixed":`${scrollPosition>=1400?"relative":""}`}`,top:0,maxWidth:width*100/70,height:`${scrollPosition>=1400?1400:700}px`}}>
<img 
style={{width:`${scrollPosition<1400?"100%":width*100/70-scrollPosition+1200}`,height:`${scrollPosition?scrollPosition-350:0}px`,position:"absolute",bottom:0,objectFit:"cover",objectPosition:"bottom",maxHeight:900}}
src={"https://www.apple.com/v/macbook-pro-16/b/images/overview/display_hero_screen__eik3891ojtoy_large.jpg"}></img>



<img style={{maxHeight:700}} src={"https://www.apple.com/v/macbook-pro-16/b/images/overview/display_pro_fallback__fkg2i8si2wyi_medium.jpg"}></img>

</div>

)
  :
  scrollPosition<1500?
<div className="grid" style={{width:scrollPosition+width,position:`${fixed?"fixed":``}`,top:0,maxWidth:width*100/70,height:700}}>  
<img 
style={{width:width*100/70-scrollPosition+1400,height:900,position:"absolute",bottom:0,objectFit:"cover",objectPosition:"bottom",minWidth:width}}
src={"https://www.apple.com/v/macbook-pro-16/b/images/overview/display_hero_screen__eik3891ojtoy_large.jpg"}></img>
</div>:
<div className="grid" style={{width:scrollPosition+width,position:"relative",top:0,maxWidth:width*100/70,height:2430}}>  
<img 
style={{width:width*100/70-scrollPosition+1400,height:900,bottom:0,   objectFit:"cover",objectPosition:"bottom",minWidth:width}}
src={"https://www.apple.com/v/macbook-pro-16/b/images/overview/display_hero_screen__eik3891ojtoy_large.jpg"}></img>
</div>
}


    
      <footer className={styles.footer}>
        <a
          href="https://vercel.com?utm_source=create-next-app&utm_medium=default-template&utm_campaign=create-next-app"
          target="_blank"
          rel="noopener noreferrer"
        >
       By orkunmertgs41@gmail.com
        </a>
      </footer>
    </div>
  )
}
